const pdfMake = require('pdfmake/build/pdfmake'); // Importa pdfmake
const pdfFonts = require('pdfmake/build/vfs_fonts'); // Importa pdfmake
pdfMake.vfs = pdfFonts.pdfMake.vfs;
const nodeMailer = require('nodemailer');
