async function generatePdf(pdfDefinition) {
    return new Promise((resolve, reject) => {
        const pdfDoc = pdfMake.createPdf(pdfDefinition);
        pdfDoc.getBuffer((buffer) => {
            resolve(buffer);
        });
    });
}

